import React, { useState } from "react";
import { Row, Col, Avatar, Input, Menu, Dropdown, Button } from "antd";
import { useAppValue } from "../context";
import _ from "lodash";
import logo from "../assets/images/ABH3D-SYS-logo.png";
import styled from "styled-components";
import {
  SearchOutlined,
  IdcardOutlined,
  BellFilled,
  AlignLeftOutlined,
} from "@ant-design/icons";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";
import Modal from "antd/lib/modal/Modal";

const Container = styled.div`
  display: flex;
  height: 60px;
  padding: 10px 20px;
  .user-profile {
    float: right;
    display: flex;
    align-items: center;
  }
  .drop-down {
    width: 100%;
  }
  .user-name {
    margin-left: 5px;
  }
  .collapse-icon {
    color: rgb(51, 202, 161);
    width: 40px;
    .anticon svg {
      height: 30px;
      width: 30px;
    }
  }
`;

const Navbar = (props) => {
  const history = useHistory();
  const [{ user }, dispatch] = useAppValue();
  const [visible, setVisible] = useState();
  const handleCancel = () => {
    setVisible(false);
  };
  const menu = (
    <Menu>
      <Menu.Item onClick={() => history.push("/user-profile")}>
        Edit Profile
      </Menu.Item>
      <Menu.Item>
        <a
          target="_blank"
          href="/#"
          rel="noopener noreferrer"
          onClick={(e) => {
            e.preventDefault();
            setVisible(true);
          }}
        >
          Change Password
        </a>
      </Menu.Item>
      <Menu.Item>
        <a
          href="/#"
          onClick={(e) => {
            e.preventDefault();
            dispatch({
              type: "logout",
            });
            history.push("/login");
          }}
        >
          Log out
        </a>
      </Menu.Item>
    </Menu>
  );
  return (
    <Container>
      <Row type="flex" style={{ width: "100%", alignItems: "center" }}>
        <Col
          span={14}
          lg={18}
          style={{ display: "flex", alignItems: "center" }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <Button
              type="link"
              className="collapse-icon"
              style={{
                color: "#87000b",
                width: 59,
                paddingLeft: 5,
                border: "none",
              }}
              onClick={() => props.onClick(!props.collapsed)}
            >
              <AlignLeftOutlined />
            </Button>
            <Link to="/">
              <img
                style={{ maxHeight: 40, maxWidth: 170 }}
                alt="logo"
                src={logo}
              />
            </Link>
          </div>
        </Col>
        <Col span={10} lg={6} style={{ display: "flex", alignItems: "center" }}>
          <Col span={3} lg={3} style={{ textAlign: "center" }}>
            {user && (
              <div onClick={(e) => e.defaultPrevented} className="user-profile">
                <Dropdown overlay={menu} placement="bottomCenter">
                  <div style={{ cursor: "pointer" }}>
                    {user.avatar?.key ? (
                      <Avatar
                      />
                    ) : (
                      <Avatar>
                        {user &&
                          _.first(user.email)}
                      </Avatar>
                    )}
                  </div>
                </Dropdown>
              </div>
            )}
          </Col>
        </Col>
      </Row>
    </Container>
  );
};

Navbar.propTypes = {};

export default Navbar;
